<script>


  export default {
    name: 'PageHeader',
    props: {
      background: String
    },
    data() {
      return {
        bgPos: [50, 60]
      }
    },
    mounted() {
      (() => {
        const bgCenterPos = [50, 60];
        const movementFactor = [50 * 0.2, 40 * 0.2];

        const headerBounds = this.$refs.header.getBoundingClientRect();
        const headerCenterPos = [
          headerBounds.x + headerBounds.width / 2,
          headerBounds.y + headerBounds.height / 2
        ];
        const headerMaxMove = [
          headerBounds.width / 2,
          headerBounds.height / 2
        ];

        const updateHeaderPos = event => {
          const moveRel = [
            (event.clientX - headerCenterPos[0]),
            (event.clientY - headerCenterPos[1])
          ];

          const moveFactor = [
            moveRel[0] / headerMaxMove[0],
            moveRel[1] / headerMaxMove[1]
          ];
          moveFactor[0] = moveFactor[0] >= 1 ? 1 : moveFactor[0];
          moveFactor[0] = moveFactor[0] <= -1 ? -1 : moveFactor[0];
          moveFactor[1] = moveFactor[1] >= 1 ? 1 : moveFactor[1];
          moveFactor[1] = moveFactor[1] <= -1 ? -1 : moveFactor[1];

          const bgCenterNew = [
            bgCenterPos[0] + movementFactor[0] * moveFactor[0],
            bgCenterPos[1] + movementFactor[1] * moveFactor[1],
          ];

          this.bgPos = bgCenterNew;
        }

        window.addEventListener("mousemove", updateHeaderPos);
        window.addEventListener("touchmove", updateHeaderPos);
      })();


    }
  }
</script>
